bool isEmptySeqStack(SeqStack* s)
{
    if (s->top == -1)
        return true;
    else
        return false;
}

//入栈，返回-1失败，0成功
int pushSeqStack(SeqStack* s, DataType x)
{
    if(s->top == MAXSIZE-1)
    {
        return -1;//栈满不能入栈
    }
    else
    {
        s->top++;
        s->data[s->top] = x;
        return 0;
    }
}

//出栈，返回-1失败，0成功
int popSeqStack(SeqStack* s, DataType* x)
{
    if(isEmptySeqStack(s))
    {
        return -1;//栈空不能出栈
    }
    else
    {
        *x = s->data[s->top];
        s->top--;
        return 0;
    }
}

//取栈顶元素，返回-1失败，0成功
int topSeqStack(SeqStack* s,DataType* x)
{
    if (isEmptySeqStack(s))
